import React, { useEffect } from "react";
import { FlatList, StyleSheet } from "react-native";

import ActivityIndicator from "../components/ActivityIndicator";
import Button from "../components/Button";
import Card from "../components/Card";
import colors from "../config/colors";
import artikalsApi from "../api/artikals";
import routes from "../navigation/routes";
import Screen from "../components/Screen";
import AppText from "../components/Text";
import useApi from "../hooks/useApi";

function ArtikalsScreen({ navigation }) {
  const getArtikalsApi = useApi(artikalsApi.getArtikals);

  useEffect(() => {
    console.log("1. useEffect");

    getArtikalsApi.request();

    console.log("2. useEffect");
  }, []);

  return (
    <>
      <ActivityIndicator visible={getArtikalsApi.loading} />
      <Screen style={styles.screen}>
        {/* <AppText>ova e vo ArtikalsScreen.js</AppText> */}
        {getArtikalsApi.error && (
          <>
            <AppText>Ne mozham da gi najdam artiklite.</AppText>
            <Button title="Retry" onPress={getArtikalsApi.request} />
          </>
        )}
        <FlatList
          data={getArtikalsApi.data}
          keyExtractor={(listing) => listing.artikal_id.toString()}
          renderItem={({ item }) => (
            <Card
              title={item.ime_artikal}
              // subTitle={"$" + item.cena}
              // imageUrl={item.images[0].url}
              // onPress={() => navigation.navigate(routes.LISTING_DETAILS, item)}
              // thumbnailUrl={item.images[0].thumbnailUrl}
            />
          )}
        />
      </Screen>
    </>
  );
}

const styles = StyleSheet.create({
  screen: {
    padding: 20,
    backgroundColor: colors.light,
  },
});

export default ArtikalsScreen;
