import client from "./client";

const endpoint = "/artikals";

const getArtikals = () => client.get(endpoint);

export const addArtikal = (artikal, onUploadProgress) => {
  const data = new FormData();
  // data.append("categoryId", artikal.category.value);
  data.append("artikal_id", artikal.artikal_id);
  data.append("vid_id", artikal.vid_id);
  data.append("ime_artikal", artikal.ime_artikal);
  data.append("cena", artikal.cena);
  data.append("steuerrelevant", artikal.cena);

  // artikal.images.forEach((image, index) =>
  //   data.append("images", {
  //     name: "image" + index,
  //     type: "image/jpeg",
  //     uri: image,
  //   })
  // );

  // if (artikal.location)
  //   data.append("location", JSON.stringify(artikal.location));

  return client.post(endpoint, data, {
    onUploadProgress: (progress) =>
      onUploadProgress(progress.loaded / progress.total),
  });
};

export default {
  addArtikal,
  getArtikals,
};
