import client from "./client";

const endpoint = "/artikals";

const getArtikals = () => {
  console.log("1. getArtikals, endpoint=${endpoint}");

  client.get(endpoint);
};

export const addArtikal = (listing, onUploadProgress) => {
  const data = new FormData();
  // data.append("categoryId", listing.category.value);
  data.append("artikal_id", listing.artikal_id);
  data.append("vid_id", listing.vid_id);
  data.append("ime_artikal", listing.ime_artikal);
  data.append("cena", listing.cena);
  data.append("steuerrelevant", listing.cena);

  // listing.images.forEach((image, index) =>
  //   data.append("images", {
  //     name: "image" + index,
  //     type: "image/jpeg",
  //     uri: image,
  //   })
  // );

  // if (listing.location)
  //   data.append("location", JSON.stringify(listing.location));

  return client.post(endpoint, data, {
    onUploadProgress: (progress) =>
      onUploadProgress(progress.loaded / progress.total),
  });
};

export default {
  addArtikal,
  getArtikals,
};
