import React, { useEffect } from "react";
import { FlatList, StyleSheet } from "react-native";

import ActivityIndicator from "../components/ActivityIndicator";
import Button from "../components/Button";
import Card from "../components/Card";
import colors from "../config/colors";
import artikalsApi from "../api/artikals";
import routes from "../navigation/routes";
import Screen from "../components/Screen";
import AppText from "../components/Text";
import useApi from "../hooks/useApi";

function ArtikalsScreen({ navigation }) {
  const getArtikalsApi = useApi(artikalsApi.getArtikals);

  useEffect(() => {
    console.log("1. useEffect");

    getArtikalsApi.request();

    console.log("2. useEffect");
  }, []);

  return (
    <Screen style={styles.screen}>
      {getArtikalsApi.error && (
        <>
          <AppText>Greshka!</AppText>
          <Button ime_artikal="Povtori" onPress={loadArtikals}></Button>
        </>
      )}
      <FlatList
        data={getArtikalsApi.data}
        keyExtractor={(item) => item.artikal_id}
        renderItem={({ item }) => (
          <Card
            key={item[0]}
            title={item[2]}
            subTitle={`Cena: ${item[3]}, Vid: ${item[1]}`}
            // image={item.image}
            onPress={() => navigation.navigate(routes.ARTIKAL_DETAILS, item)}
            // onPress={() => console.log(item[3])}
          />
        )}
      />
    </Screen>
  );
}

const styles = StyleSheet.create({
  screen: {
    padding: 20,
    backgroundColor: colors.light,
  },
});

export default ArtikalsScreen;
